      function CF_selection_ruleFormula return Char is
  v_return varchar2(30);
begin
  if :include_exclude_ind = 'I' then
  	v_return := 'Include All';
  elsif :include_exclude_ind = 'X' then
  	v_return := 'Exclude All';
  elsif :include_exclude_ind = 'R' then
  	v_return := 'By Rules (see below)';
  else
  	v_return := 'Referenced records only';
  end if;
  return v_return;
end;

