      -- Begin Fix - SCR 5918.04 - Eswar KC - 14-May-2013
FUNCTION CF_rnpFormula
   RETURN VARCHAR2
IS
   l_nrnp       VARCHAR2 (25);
   l_ndot       NUMBER;
   l_nrnp_nav   NUMBER;
BEGIN
   SELECT rnp
     INTO l_nrnp_nav
     FROM navdb
    WHERE navdb_id = :P_navdbid;

   SELECT INSTR (l_nrnp_nav, '.') INTO l_ndot FROM DUAL;
       

   IF l_ndot = 0 AND l_nrnp_nav <> 0 
   THEN
      l_nrnp := l_nrnp_nav || '.0';
   ELSIF l_ndot <>  0 AND l_nrnp_nav < 1 THEN
      l_nrnp := '0' || l_nrnp_nav;
   ELSE
   	  l_nrnp := l_nrnp_nav;
   END IF;
   RETURN l_nrnp;

 EXCEPTION
   	WHEN OTHERS THEN
   	 l_nrnp := 'None Selected';
     RETURN (l_nrnp);
END;
-- End Fix - SCR 5918.04 - Eswar KC - 14-May-2013

